import React, { useState } from 'react';
import styled from "styled-components";

const Input = styled.input`
position: absolute;
width: 100%;
height: 100%;
top: 0;
left: 0;
opacity: 0;
cursor: pointer;
`;

const Container = styled.div`
position: relative;
display: grid;
grid-template-columns: 0.4fr 0.6fr;
grid-column-gap: 2rem;
grid-template-areas:
    'icon text'
    'icon subtext';
    margin-left: auto;
    margin-right: auto;
    padding: 7rem 5rem;
width: 40rem;
box-sizing: border-box;
background-color: #f8f8f8;
border-radius: 1rem;
margin-bottom: 1.5rem;
margin-top: 1.5rem;
`;

const H3 = styled.header`
grid-area: text;
    align-self: end;
    font-weight: 700px;
    text-align: left;
    margin-bottom: 0.25rem;
    color: rgba(85, 90, 100, 0.5);
    transition: color 150ms ease-out;
    font-family: Calibri;
`;

const Image = styled.img`
  display: inline-block;
`;


const AddFile = () => {
  const [file,setFile]=useState('')


  return (
    <Container>
        <img src="upload-cloud.png" />
        <H3>Drag &amp; Drop</H3>
        <input type="file" id="fileElem" accept={".csv,.xls, .xlsx"} onChange={(event)=>setFile(event.target.value)}/><br />
        </Container>
  //  <Input id="data-file" accept=".csv" type="file" />
  );
}

export default AddFile;
