import React, { useState } from 'react';
import { fixture } from '../test/fixture.js';
import { Importer, ImporterField } from 'react-csv-importer'
// const apiUrl = "https://run.mocky.io/v3/cbca762d-3f84-4ae4-bb26-79fa774a6c72";
const apiUrl = "";

const field = [
    {
        name: 'Beneficiary ID',
        label: 'BenID',
        optional: false
    },
    {
        name: 'Payment Currency',
        label: 'currency',
        optional: false
    },
    {
        name: 'Payment Amount',
        label: 'amount',
        optional: false
    },
    {
        name: 'Payment Reference',
        label: 'ref',
        optional: false
    },
];


const ImportCSV = async () => {
    const [notification, setNotification] = useState({
		open: false,
		vertical: 'top',
		horizontal: 'right',
		severity: 'success',
		message: ''
	})

    const showToastNotification = (file, fields) => {
		setNotification({
			...notification,
			open: true,
			message: `Le fichier ${file.name} a bien été importé.`
		})
	}


    // add csv uploader
    <Importer
				chunkSize={10000} // optional, internal parsing chunk size in bytes
				assumeNoHeaders={false} // optional, keeps "data has headers" checkbox off by default
				restartable={true} // optional, lets user choose to upload another file when import is complete
				processChunk={async rows => {
					for (let row of rows) {
						console.log(row)
					}
				}}
				onComplete={({ file, fields }) =>
					// optional, invoked right after import is done (but user did not dismiss/reset the widget yet)
					showToastNotification(file, fields)
				}
				// onClose={() => {
				// 	goToNextPage()
				// }}
				// CSV options passed directly to PapaParse if specified:
				// delimiter={...}
				// newline={...}
				// quoteChar={...}
				// escapeChar={...}
				// comments="test"
				// skipEmptyLines={...}
				// delimitersToGuess={...}
			>
				{field.map(({ name, label, optional }, idx) =>
					optional ? (
						<ImporterField key={idx} name={name} label={label} optional />
					) : (
						<ImporterField key={idx} name={name} label={label} />
					)
				)}
			</Importer>

    const data = fixture;//fileUpload;
    console.log(data);


    const handleSubmit = async () => {
        try {
            const result = await fetch(apiUrl, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(data)
            });

            if (result.ok) {
                console.log(result);
            } else {
                throw new Error();
            }
        } catch (e) {
            console.log(e);
        }
    };

};

export default ImportCSV;
