import React from "react";
// External
import toast from "react-hot-toast";

// Components
import CustomIcon from "./custom-icon";
import styled from "styled-components";

const NotificationCard = styled.div`
  background: ${(props) => props.bgColor};
  border-radius: 32px;
  height: 132px;
  width: 438px;
  position: relative;
  overflow: hidden;
`;

const NotificationImage = styled.img``;
const NotificationImageWrapper = styled.div`
  margin-top: -32px;
  margin-left: 24px;
  position: absolute;
  z-index: 2;
`;

const NotificationContent = styled.div`
  color: white;
  position: absolute;
  right: 40px;
  top: 0px;
  width: 244px;
`;

const NotificationTitle = styled.h1`
  color: white;
`;

const NotificationDescription = styled.p`
  color: white;
`;

const NotificationIconButton = styled.button`
  background: transparent;
  border: none;
  position: absolute;
  right: 16px;
  top: 16px;
  cursor: pointer;
  border-radius: 50%;
`;


const ToastNotification = ({
  t,
  bgColor,
  icon,
  title,
  message,
}) => {
  // handlers
  const handleDismiss = () => {
    toast.dismiss(t.id);
  };

  return (
    <>
      <NotificationImageWrapper>
        <NotificationImage src={icon} alt="" />
      </NotificationImageWrapper>
      <NotificationCard bgColor={bgColor}>
        <NotificationContent>
          <NotificationTitle>{title}</NotificationTitle>
          <NotificationDescription>{message}</NotificationDescription>
        </NotificationContent>
        <NotificationIconButton onClick={handleDismiss}>
          <CustomIcon icon="times" />
        </NotificationIconButton>
      </NotificationCard>
    </>
  );
};

export default ToastNotification;
