import React from "react";
import styled from "styled-components";

// External
import toast from "react-hot-toast";

// Components
import CustomIcon from "./CustomIcon";

// Styles
export const NotificationCard = styled.div`
  background: ${props => props.bgColor};
  border-radius: 22px;
  height: 100px;
  width: 438px;
  position: relative;
  overflow: hidden;
`;
export const NotificationImageWrapper = styled.div`
  margin-top: -32px;
  margin-left: 24px;
  position: absolute;
  z-index: 2;
`;

export const NotificationContent = styled.div`
  color: white;
  position: absolute;
  right: 40px;
  top: 0px;
  width: 244px;
  // margin-top: -40px;
  // margin-left: 120px;
`;
export const NotificationTitle = styled.h1`
  color: white;
  font-family: Calibri
`;
export const NotificationDescription = styled.p`
  color: white;
  font-family: Calibri
`;
export const NotificationIconButton = styled.button`
  background: transparent;
  border: none;
  position: absolute;
  right: 16px;
  top: 16px;
  cursor: pointer;
  border-radius: 50%;
`;
export const NotificationImage = styled.img`
    width: 20px;
    opacity: 0;
    height: 20px;
    border-radius: 10px;
    position: relative;
`;



const ToastNotification = ({
  t,
  bgColor,
  title,
  message,
  icon
}) => {
  // handlers
  const handleDismiss = () => {
    toast.dismiss(t.id);
  };

  return (
    <>
       <NotificationImageWrapper>
        <NotificationImage src={icon} alt=""  />
      </NotificationImageWrapper>
      <NotificationCard bgColor={bgColor}>
        <NotificationContent>
          <NotificationTitle>{title}</NotificationTitle>
          <NotificationDescription>{message}</NotificationDescription>
        </NotificationContent>
        <NotificationIconButton onClick={handleDismiss}>
          <CustomIcon icon="times" />
        </NotificationIconButton>
      </NotificationCard>
    </>
  );
};

export default ToastNotification;
