import { useCallback } from 'react';
import { toast } from 'react-toastify';

const toastOptions = {
  autoClose: 4000,
  hideProgressBar: true,
  position: toast.POSITION.TOP_CENTER,
  closeButton: false,
};

const toastSuccessOptions = {
  style: {
    backgroundColor: '#F4F9F6',
    border: '2px solid #258244',
    borderRadius: '4px',
    color: '#444444',
    fontSize: '13px',
  },
};

const toastErrorOptions = {
  style: {
    backgroundColor: '#FCF3F5',
    border: '2px solid #D40C3C',
    borderRadius: '4px',
    color: '#444444',
    fontSize: '13px',
  },
};

export const useToast =  {
    success,
    error
}

  function success(message) {
    return toast.success(message, { ...toastOptions, ...toastSuccessOptions });
  };

  function error(message){
   return toast.error(message, { ...toastOptions, ...toastErrorOptions });
  };
