import React, { useState } from 'react';
import styled from "styled-components";

const Input = styled.input`
position: absolute;
width: 100%;
height: 100%;
top: 0;
left: 0;
opacity: 0;
cursor: pointer;
`;

const Container = styled.div`
position: relative;
display: grid;
grid-template-columns: 0.4fr 0.6fr;
grid-column-gap: 2rem;
grid-template-areas:
    'icon text'
    'icon subtext';
margin: 1.5rem 0rem;
padding: 2rem 5rem;
/* width: 100%; */
width: 40rem;
box-sizing: border-box;
background-color: #f8f8f8;
border-radius: 1rem;
`;

const SVG = styled.svg`
justify-self: center;
align-self: center;
grid-area: icon;
height: 60%;
max-width: 100%;
opacity: 0.2;
pointer-events: none;
-webkit-user-select: none;
-moz-user-select: none;
-ms-user-select: none;
user-select: none;
-webkit-user-drag: none;
`;


const AddFile = () => {

  return (
    <Container>
        <h3 >Drag &amp; Drop a File</h3>
    </Container>
  //  <Input id="data-file" accept=".csv" type="file" />
  );
}

export default AddFile;
