import React, {useState, useEffect, useMemo} from 'react';
// import AddFile  from '../utils/AddFile.js';
import Table, { TableComponent } from '../components/TableComponent.js';
import styled from "styled-components";
import AddFile from '../utils/AddFile.js';
import ToastNotification from '../components/ToastNotification.js';

import successImage from '../images/tickIcon.png';

// const apiUrl = "https://run.mocky.io/v3/cbca762d-3f84-4ae4-bb26-79fa774a6c72";
const apiUrl = "";

const Container = styled.div`
    flex-grow: 1;
`;

const Header = styled.header`
    padding: 8px;
    margin-left: auto;
    margin-right: auto;
    font-family: Calibri;
`;

const Section = styled.div`
    margin-left: auto;
    margin-right: auto;
`;

const Button = styled.button`
    border-color: #1d8676;
    background: #1d8676;
    border-style: solid;
    padding: 8px;
    align-items: center;
    font-weight: 700;
    font-family: Calibri;
    display: inline-flex;
    line-height: 1.5;
    border-top-left-radius: 0.4rem;
    border-top-right-radius: 0.4rem;
    border-bottom-right-radius: 0.4rem;
    border-bottom-left-radius: 0.4rem;
    color: white;
    border-width: 0.1rem;
    justify-content: center;
    transition: 0.2s ease;
    width: 10%;
`;


const HomePage = () => {
  // data state to store the API data. Its initial value is an empty array
    const body = ""; //fileUpload;
    console.log(body);


    const handleSubmit = async () => {

        try {
            const result = await fetch(apiUrl, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(body)
            });

            if (result.ok) {
                toast.custom(t => (
                <ToastNotification 
                t={t}
                icon={successImage}
                bgColor="#5FA772"
                title={"Payment Successful"}
                message={"Payments are processing, You will be notified when they are ready"}
                />));
                // console.log(result);
            } else {
                throw new Error();
            }
        } catch (e) {
            console.log(e);
        }
    };

    return (
		<>
			<Container>
                    <Header>
                        CSV File Uploader
                    </Header>  
                    <Section>

                    <AddFile/>
                    {/* <TableComponent /> */}
                        <Button onClick={handleSubmit}>Import</Button>
                    </Section>  
            </Container>
		</>
	)
};

export default HomePage;
