import React, { useState, useEffect } from 'react';
import styled from "styled-components";
import AddFile from '../utils/AddFile.js';
import toast from "react-hot-toast";
import ToastNotification from "../components/ToastNotification";

import tickIcon from "../images/tickIcon.png";

// Constants
import {
    variants,
    DEFAULT_TITLE,
    DEFAULT_DESCRIPTION,
} from "../components/ToastData";


// const apiUrl = "https://run.mocky.io/v3/cbca762d-3f84-4ae4-bb26-79fa774a6c72";
const apiUrl = "";

const Container = styled.div`
    flex-grow: 1;
`;

const Header = styled.header`
    padding: 8px;
    margin-left: auto;
    margin-right: auto;
    font-family: Calibri;
`;

const Section = styled.div`
    margin-left: auto;
    margin-right: auto;
`;

const Button = styled.button`
    border-color: #1d8676;
    background: #1d8676;
    border-style: solid;
    padding: 8px;
    align-items: center;
    font-weight: 700;
    font-family: Calibri;
    display: inline-flex;
    line-height: 1.5;
    border-top-left-radius: 0.4rem;
    border-top-right-radius: 0.4rem;
    border-bottom-right-radius: 0.4rem;
    border-bottom-left-radius: 0.4rem;
    color: white;
    border-width: 0.1rem;
    justify-content: center;
    transition: 0.2s ease;
    width: 10%;
    margin-bottom: 1.5rem;
`;


const HomePage = () => {
    // data state to store the API data. Its initial value is an empty array
    const body = ""; //fileUpload;
    console.log(body);
    const [currentVariant, setCurrentVariant] = useState(variants.SUCCESS);
    const [title, setTitle] = useState(DEFAULT_TITLE.SUCCESS);
    const [message, setMessage] = useState(DEFAULT_DESCRIPTION.Success);

    // change title and message when notification variant changes
    useEffect(() => {
        setTitle(DEFAULT_TITLE[currentVariant]);
        setMessage(DEFAULT_DESCRIPTION[currentVariant]);
    }, [currentVariant]);


    const handleSubmit = async () => {
        toast.custom(() => (
            <ToastNotification
                icon={tickIcon}
                bgColor="#5FA772"
                title={DEFAULT_TITLE.Success}
                message={DEFAULT_DESCRIPTION.Success}
            />
        ));





        try {
            //     const result = await fetch(apiUrl, {
            //         method: 'POST',
            //         headers: {
            //             'Content-Type': 'application/json'
            //         },
            //         body: JSON.stringify(body)
            //     });

            //     if (result.ok) {


            // } else {
            //     throw new Error();
            // }
        } catch (e) {
            console.log(e);
            toast.custom(() => (
                <ToastNotification
                    icon={tickIcon}
                    bgColor="#B95656"
                    title={DEFAULT_TITLE.Fail}
                    message={DEFAULT_DESCRIPTION.Fail}
                />
            ));
        }
    };

    return (
        <>
            <Container>
                <Header>
                    CSV File Uploader
                </Header>
                <Section>

                    <AddFile />
                    <Button onClick={handleSubmit}>Submit</Button>
                </Section>
            </Container>
        </>
    )
};

export default HomePage;
