import React, { useState } from 'react';
import styled from "styled-components";

const Input = styled.input`
position: absolute;
width: 100%;
height: 100%;
top: 0;
left: 0;
opacity: 0;
cursor: pointer;
`;

const Container = styled.div`
position: relative;
display: grid;
grid-template-columns: 0.4fr 0.6fr;
grid-column-gap: 2rem;
grid-template-areas:
    'icon text'
    'icon subtext';
margin: 1.5rem 0rem;
padding: 2rem 5rem;
/* width: 100%; */
width: 40rem;
box-sizing: border-box;
background-color: #f8f8f8;
border-radius: 1rem;
`;

const SVG = styled.svg`
justify-self: center;
align-self: center;
grid-area: icon;
height: 60%;
max-width: 100%;
opacity: 0.2;
pointer-events: none;
-webkit-user-select: none;
-moz-user-select: none;
-ms-user-select: none;
user-select: none;
-webkit-user-drag: none;
`;


const AddFile = () => {

  return (
    <Container>
        <svg  width="100%" height="100%" viewBox="0 0 18 18" version="1.1" xmlns="http://www.w3.org/2000/svg" style="fill-rule: evenodd; clip-rule: evenodd; stroke-linejoin: round; stroke-miterlimit: 2;"><g><rect x="0" y="0" width="18" height="18" style="fill: none;"></rect></g><g><g><path d="M10.5,1.5L4.5,1.5C3.675,1.5 3.008,2.175 3.008,3L3,15C3,15.825 3.667,16.5 4.493,16.5L13.5,16.5C14.325,16.5 15,15.825 15,15L15,6L10.5,1.5ZM13.5,15L4.5,15L4.5,3L9.75,3L9.75,6.75L13.5,6.75L13.5,15ZM6,11.258L7.058,12.315L8.25,11.13L8.25,14.25L9.75,14.25L9.75,11.13L10.942,12.323L12,11.258L9.008,8.25L6,11.258Z" style="fill: rgb(85, 90, 100); fill-rule: nonzero;"></path></g></g></svg>
    </Container>
  //  <Input id="data-file" accept=".csv" type="file" />
  );
}

export default AddFile;
