import React, { useState, useRef} from 'react';
import styled from "styled-components";


const Container = styled.div`
position: relative;
display: grid;
grid-template-columns: 0.4fr 0.6fr;
grid-column-gap: 2rem;
grid-template-areas:
    'icon text'
    'icon subtext';
    margin-left: auto;
    margin-right: auto;
    padding: 7rem 5rem;
width: 40rem;
box-sizing: border-box;
background-color: #f8f8f8;
border-radius: 1rem;
margin-bottom: 1.5rem;
margin-top: 1.5rem;
border: 2px dashed #ccc;
`;

const H3 = styled.header`
grid-area: text;
    align-self: end;
    font-weight: 700px;
    text-align: left;
    margin-bottom: 0.25rem;
    color: rgba(85, 90, 100, 0.5);
    transition: color 150ms ease-out;
    font-family: Calibri;
`;

const Image = styled.img`
  display: inline-block;
`;

const Input = styled.input`
display: none;

`;

const AddFile = () => {
  const fileInputField = useRef(null);
  const [files, setFiles] = useState({});




  return (
    <Container>
        <img src="upload-cloud.png" />
        <H3>Drag &amp; Drop</H3>
        <Input type="file" ref={fileInputField} accept={".csv,.xls, .xlsx"} onChange={(event)=>setFile(event.target.value)}/><br />
        </Container>
  //  <Input id="data-file" accept=".csv" type="file" />
  );
}

export default AddFile;
