import React, { useState, useEffect } from 'react';
import styled from "styled-components";
import Papa from "papaparse";
import ToastNotification from "../components/ToastNotification";


import {
  variants,
  DEFAULT_TITLE,
  DEFAULT_DESCRIPTION,
} from "../components/ToastData";

const FileContainer = styled.div`
position: relative;
display: grid;
grid-template-columns: 0.4fr 0.6fr;
grid-column-gap: 2rem;
grid-template-areas:
    'icon text'
    'icon subtext';
    margin-left: auto;
    margin-right: auto;
    padding: 7rem 5rem;
width: 40rem;
box-sizing: border-box;
background-color: #f8f8f8;
border-radius: 1rem;
margin-bottom: 1.5rem;
margin-top: 1.5rem;
border: 2px dashed #ccc;
`;

const H3 = styled.header`
    grid-area: text;
    align-self: end;
    font-weight: 700px;
    text-align: left;
    margin-bottom: 0.25rem;
    color: rgba(85, 90, 100, 0.5);
    transition: color 150ms ease-out;
    font-family: Calibri;
`;

const Image = styled.img`
  display: inline-block;
`;

const Container = styled.div`
  position: relative;
`;

const P = styled.p`
text-align: left;
`

const Input = styled.input`
display: none;
  display: block;
  width: 100%;
  border: none;
  text-transform: none;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  opacity: 0;

  &:focus {
    outline: none;
  }

`;

const Table = styled.table`
font-family: Calibri;
margin-left: auto;
margin-right: auto;
margin-bottom: 1.5rem;
th {
  text-transform: uppercase;
  font-weight: 400
}
td, th {
  border-top-color: #F0F0F0;
    border-top-style: solid;
  text-align: left;
  padding: 10px;
}
`;

const AddFile = () => {
  // State to store parsed data
  const [parsedData, setParsedData] = useState([]);
  // column names
  const [tableRows, setTableRows] = useState([]);
  //State to store the values
  const [values, setValues] = useState([]);
  const [currentVariant, setCurrentVariant] = useState(variants.SUCCESS);
  const [title, setTitle] = useState(DEFAULT_TITLE.SUCCESS);
  const [message, setMessage] = useState(DEFAULT_DESCRIPTION.Success);

  // change title and message when notification variant changes
  useEffect(() => {
      setTitle(DEFAULT_TITLE[currentVariant]);
      setMessage(DEFAULT_DESCRIPTION[currentVariant]);
  }, [currentVariant]);


  const changeHandler = (event) => {
    console.log(event.target.files[0])

    // Passing file data (event.target.files[0]) to parse using Papa.parse
    Papa.parse(event.target.files[0], {
      header: true,
      skipEmptyLines: true,
      complete: function (results) {
        console.log(results.data);
        const rowsArray = [];
        const valuesArray = [];

        // Iterating data to get column name and their values
        results.data.map((d) => {
          rowsArray.push(Object.keys(d));
          valuesArray.push(Object.values(d));
        });

        // Parsed Data Response in array format
        setParsedData(results.data);

        // Filtered Column Names
        setTableRows(rowsArray[0]);

        // Filtered Values
        setValues(valuesArray);
      },
    });
  };

  const handleSubmit = async () => {
    toast.custom(() => (
        <ToastNotification
            icon={tickIcon}
            bgColor="#5FA772"
            title={DEFAULT_TITLE.Success}
            message={DEFAULT_DESCRIPTION.Success}
        />
    ));

    try {
        //     const result = await fetch(apiUrl, {
        //         method: 'POST',
        //         headers: {
        //             'Content-Type': 'application/json'
        //         },
        //         body: JSON.stringify(body)
        //     });

        //     if (result.ok) {


        // } else {
        //     throw new Error();
        // }
    } catch (e) {
        console.log(e);
        toast.custom(() => (
            <ToastNotification
                icon={tickIcon}
                bgColor="#B95656"
                title={DEFAULT_TITLE.Fail}
                message={DEFAULT_DESCRIPTION.Fail}
            />
        ));
    }
};



  return (
    <Container>
      <FileContainer>
        <H3>Drag &amp; Drop </H3>

        <Input
          type="file"
          name="file"
          accept=".csv"
          onChange={changeHandler}
        />

      </FileContainer>
      <Button onClick={handleSubmit}>Submit</Button>


      <Table>
        <thead>
          <tr>
            {tableRows.map((rows, index) => {
              return <th key={index}>{rows}</th>;
            })}
          </tr>
        </thead>
        <tbody>
          {values.map((value, index) => {
            return (
              <tr key={index}>
                {value.map((val, i) => {
                  return <td key={i}>{val}</td>;
                })}
              </tr>
            );
          })}
        </tbody>
      </Table>

    </Container>
  );
}

export default AddFile;
