import React from "react";
// External
import toast from "react-hot-toast";

// Components
import CustomIcon from "./CustomIcon";
import styled from "styled-components";

const NotificationContainer = styled.div`
  position: fixed;
  box-sizing: border-box;
  font-size: 14px;
`;

const NotificationImage = styled.img`
    float: left;
    margin-right: 15px;
`;
const NotificationImageWrapper = styled.div`
  margin-top: -32px;
  margin-left: 24px;
  position: absolute;
  z-index: 2;
`;

const NotificationContent = styled.div`
  color: white;
  position: absolute;
  right: 40px;
  top: 0px;
  width: 244px;
`;

const NotificationTitle = styled.p`
    font-weight: 700;
    font-size: 16px;
    text-align: left;
    margin-top: 0;
    margin-bottom: 6px;
    width: 300px;
    height: 18px;
`;

const NotificationDescription = styled.p`
  margin: 0;
  text-align: left;
  height: 18px;
  margin-left: -1px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
`;

const NotificationIconButton = styled.button`
  background: transparent;
  border: none;
  position: absolute;
  right: 16px;
  top: 16px;
  cursor: pointer;
  border-radius: 50%;
`;


